const blockEmpty = document.querySelector('.schedule-empty');
const tableSchedule = document.querySelector('.table-schedule');
blockEmpty.innerHTML = scheduleEmpty;
tableSchedule.classList.add('d-none');
// form
const form = document.querySelector('.form-schedule');
const btn = form
  .querySelector('[data-schedule="schedule"]')
  .getAttribute('data-schedule');

// scroll
const onScrollFormToSchedule = btn => {
  document.getElementById(btn).scrollIntoView({
    behavior: 'smooth',
    inline: 'start',
  });
};

form.addEventListener('submit', e => {
  e.preventDefault();

  if (document.querySelector('.schedule')) {
    blockEmpty.innerHTML = '';
    tableSchedule.classList.remove('d-none');
    navMobBtnText.innerText = nowDay;
  }

  onScrollFormToSchedule(btn);

});

form.addEventListener('reset', e => {
  e.preventDefault();
  blockEmpty.innerHTML = scheduleEmpty;
  tableSchedule.classList.add('d-none');
});


let objectDate = new Date();
let day = objectDate.getDate();
let month = objectDate.getMonth() + 1;
if (day < 10) {
  day = '0' + day;
}

if (month < 10) {
  month = `0${month}`;
}
let toDay = day + '.' + month;

console.log(toDay);

const days = [
  'воскресенье',
  'понедельник',
  'вторник',
  'среда',
  'четверг',
  'пятница',
  'суббота',
];

const mobNameDays = ['вс', 'пн', 'вт', 'ср', 'чт', 'пт', 'сб'];

const nowDay = toDay + ' ' + days[objectDate.getDay()];

const navMob = document.querySelector('.schedule-nav');
const navMobBtn = navMob.querySelector('.schedule-nav__button');
const navMobBtnText = navMob.querySelector('.schedule-nav__button span');
const navMobWrapper = navMob.querySelector('.schedule-nav__wrapper');
const navMobList = navMob.querySelector('.schedule-nav__list');
const navMobListItems = navMobList.querySelectorAll('.schedule-nav__item');
navMob.classList.remove('active');
const tdItems = document.querySelectorAll('.table-schedule__td:not(.time)');
let mql = window.matchMedia('(min-width: 600px)');

navMobBtnText.innerText = nowDay;
navMobBtn.addEventListener('click', () => {
  navMob.classList.toggle('active');
});

[...navMobListItems].map(el => {
  el.addEventListener('click', () => {
    toDay = el.querySelector('span').innerText;
    navMobBtnText.innerText = el.innerText;
    navMob.classList.toggle('active');

    [...tdItems].map(el => {
      el.classList.remove('active');
      let elAttr = el.getAttribute('data-day');
      if (elAttr === toDay) {
        el.classList.toggle('active');
      }
    });
  });
});

[...tdItems].map(el => {
  el.classList.remove('active');
  let elAttr = el.getAttribute('data-day');

  if (el.innerText === '' && !mql.matches) {
    el.classList.add('d-none');

  }

  if (mql.matches) {
    el.classList.remove('d-none');
  }

  if (elAttr === toDay) {
    el.classList.add('active');
  }
});

const buttomNav = document.querySelector('.mob-week-days');
const buttomNavItems = document.querySelectorAll('.mob-week-days__item');
console.log(mobNameDays[objectDate.getDay()]);

[...buttomNavItems].map(el => {
  el.classList.remove('active');
  if (el.innerText === mobNameDays[objectDate.getDay()]) {
    el.classList.add('active');
  }

  el.addEventListener('click', () => {
    el.classList.toggle('active');
  });
});

// resize
window.addEventListener('resize', function(e) {
  if (mql.matches) {
    [...tdItems].map(el => {
      el.classList.remove('d-none');
    });
  } else {
    [...tdItems].map(el => {
      if (el.innerText === '') {
        el.classList.add('d-none');
      }
    });

  }
});

